// Resilient JSON parser for n8n Code node
function tryParseJson(str) {
  try {
    return { ok: true, parsed: JSON.parse(str) };
  } catch (e) {
    return { ok: false, error: e.message };
  }
}

function cleanString(s) {
  if (typeof s !== "string") return s;
  // normalize smart quotes, remove code fences, trim
  s = s.replace(/```json|```/g, "");
  s = s.replace(/[‚Äú‚Äù]/g, '"').replace(/[‚Äò‚Äô]/g, "'");
  // remove stray control characters except newline/tab
  s = s.replace(/[\u0000-\u0008\u000B\u000C\u000E-\u001F]/g, "");
  // remove trailing commas before } or ]
  s = s.replace(/,(\s*[}\]])/g, "$1");
  return s.trim();
}

function extractLargestJsonSubstring(s) {
  // find all substrings that start with { and end with }
  let candidates = [];
  for (let i = 0; i < s.length; i++) {
    if (s[i] !== "{") continue;
    let depth = 0;
    for (let j = i; j < s.length; j++) {
      if (s[j] === "{") depth++;
      else if (s[j] === "}") {
        depth--;
        if (depth === 0) {
          candidates.push(s.slice(i, j + 1));
          break;
        }
      }
    }
  }
  // fallback: if none found, try from first { to end
  if (candidates.length === 0) {
    const first = s.indexOf("{");
    if (first !== -1) return s.slice(first);
    return null;
  }
  // return longest candidate
  candidates.sort((a,b)=>b.length-a.length);
  return candidates[0];
}

function balanceBraces(s) {
  const opens = (s.match(/{/g) || []).length;
  const closes = (s.match(/}/g) || []).length;
  if (opens > closes) {
    return s + "}".repeat(opens - closes);
  }
  return s;
}

// Gather possible fields to try
const input = $input.first();
const candidates = [];

// push common locations
const maybePaths = [
  input?.json?.output,
  input?.json?.text,
  input?.json?.response,
  input?.json?.result,
  input?.json?.data,
  input?.json?.choices,
  input?.json?.completion,
  input?.json?.body,
  input?.json
];

for (const p of maybePaths) {
  if (p === undefined || p === null) continue;
  if (typeof p === "object") {
    // stringify objects
    candidates.push(JSON.stringify(p));
  } else if (typeof p === "string") {
    candidates.push(p);
  }
}

// also try the raw input dump
try { candidates.push(JSON.stringify(input)); } catch(e){}

// attempt parsing candidates
let final = null;
let diagnostics = [];

for (let rawCandidate of candidates) {
  if (!rawCandidate || typeof rawCandidate !== "string") continue;
  let cleaned = cleanString(rawCandidate);
  // If looks like JSON object inside, extract it
  let extracted = extractLargestJsonSubstring(cleaned) || cleaned;
  // try parse as-is
  let attempt = tryParseJson(extracted);
  if (!attempt.ok) {
    // try balancing braces then parse
    let balanced = balanceBraces(extracted);
    attempt = tryParseJson(balanced);
    if (attempt.ok) {
      final = attempt.parsed;
      diagnostics.push({ sourceSample: extracted.slice(0,300), method: "balanced" });
      break;
    }
  } else {
    final = attempt.parsed;
    diagnostics.push({ sourceSample: extracted.slice(0,300), method: "direct" });
    break;
  }
  diagnostics.push({ sourceSample: extracted.slice(0,300), error: attempt.error });
}

// Return result or detailed error
if (final) {
  return [{ json: { ok: true, parsed: final, diagnostics } }];
} else {
  // helpful debug info for you to paste back to LLM or inspect
  return [{
    json: {
      ok: false,
      error: "Could not parse JSON from any candidate. See diagnostics.",
      diagnostics,
      // include the raw 'output' sample that you showed earlier for easier debugging:
      received: input?.json?.output ?? input?.json?.text ?? input?.json
    }
  }];
}
